import React from 'react';

const Card = ({id, name, email}) =>
{
    return(
        <div className= "f1 bg-light-purple tc dib br3 pa3 ma2 grow bw2 shadow5">
            <img alt='robots' src={`https://robohash.org/${id}?size=200x30000`}/>
            <div>
                <h2>{name}</h2>
                <p>{email}</p>
            </div>
        </div>
    );
}

export default Card;
